$(document).ready(function(){setTimeout(function(){$('[data-search]').on('keyup',function(){var searchVal=$(this).val();var filterItems=$('[data-filter-item]');if(searchVal!==''){filterItems.addClass('hidden');$('[data-filter-item][data-filter-name*="'+searchVal.toLowerCase()+'"]').removeClass('hidden');$('.term').text("#"+searchVal.toLowerCase())}else{filterItems.removeClass('hidden');$('.term').text('')}});showimg();$("#viewlist, #viewgrid").on('click',function(){$("#grid-view").toggleClass("list masonry");if($(this).attr('id')=="viewlist"){$(this).hide();$('#viewgrid').show()}else{$(this).hide();$('#viewlist').show()}});function showimg(){$(".hides img").each(function(index){var times=(index*5);setTimeout(function(){$(".hides img").eq(index).addClass("show-it");$(".item").eq(index).removeClass("bgload")},times)})}
$('.search-container i.fa.fa-search, .search-container i.fa.fa-times').on('click',function(){$('.search-container').toggleClass('open')});$('.item').on('click',function(){$('body').toggleClass('open-modal');$('.modal-containner img').attr('src',$('img',this).attr('src'));$('.modal-containner .text-modal').text($('.title',this).text())});$('.close-window').on('click',function(){$('body').removeClass('open-modal')})},3000)})
